fn find_uniq(input: &str, len: usize) {
    let mut buffer = Vec::new();

    for (i, c) in input.chars().enumerate() {
        buffer.push(c);
        if buffer.len() > len {
            buffer.remove(0);
        }
        
        let mut check = buffer.clone();
        check.sort();
        check.dedup();
        if buffer.len() == len && buffer.len() == check.len() {
            println!("Result: {}", i+1);
            break;
        }
    }
}

fn main() {
    let input = "";
    find_uniq(&input, 4);
    find_uniq(&input, 14);
}
